import './App.css';
import React from 'react';
import Navigation from '../components/Navigation/Navigation.js';
import Logo from '../components/Logo/Logo.js';
import Rank from '../components/Rank/Rank.js';
import Input from '../components/Input/Input.js';
import Image from '../components/Image/Image.js';
import logo from './logo.svg';
import SignInForm from '../components/SignInForm/SignInForm.js';
import RegisterForm from '../components/RegisterForm/RegisterForm.js';
import Particles from 'react-tsparticles';

const particles_option = {
id:"tsparticles",
options:{
          background: {
          color: {
            value: "#0d47a1",
          },
        },
        fpsLimit: 60,
        interactivity: {
          events: {
            onClick: {
              enable: true,
              mode: "push",
            },
            onHover: {
              enable: true,
              mode: "repulse",
            },
            resize: true,
          },
          modes: {
            bubble: {
              distance: 400,
              duration: 2,
              opacity: 0.8,
              size: 40,
            },
            push: {
              quantity: 4,
            },
            repulse: {
              distance: 200,
              duration: 0.4,
            },
          },
        },
        particles: {
          color: {
            value: "#ffffff",
          },
          links: {
            color: "#ffffff",
            distance: 150,
            enable: true,
            opacity: 0.5,
            width: 1,
          },
          collisions: {
            enable: true,
          },
          move: {
            direction: "none",
            enable: true,
            outMode: "bounce",
            random: false,
            speed: 6,
            straight: false,
          },
          number: {
            density: {
              enable: true,
              area: 800,
            },
            value: 80,
          },
          opacity: {
            value: 0.5,
          },
          shape: {
            type: "circle",
          },
          size: {
            random: true,
            value: 5,
          },
        },
        detectRetina: true,
}};
class App extends React.Component{
  constructor(){
    super();
    this.state = {
      searchField : '',
      imgURL:logo,
      webpage: 'sign_out',
    }
  }

  onSearchChange = (event) =>{
    this.setState({searchField:event.target.value})
  }

  onPageChange = (page) =>{
    this.setState({webpage:page})
  }

  onButtonClick = (event) => {
    this.setState({imgURL: this.state.searchField})
    console.log('click')

  }

  render() {
    const {imgURL, webpage} = this.state;
    return(
    <div className="App">
        <Particles params={particles_option} />
        <Navigation PageChange={this.onPageChange} Webpage={webpage}/>
        {webpage ==='sign_in' ?
          <div>
            <Logo />
            <Rank />
            <Input searchChange={this.onSearchChange} buttonClick={this.onButtonClick}/>
            <Image imgURL={imgURL}/>
          </div>:
          ( webpage === 'sign_out' ?
            <SignInForm PageChange={this.onPageChange} />: <RegisterForm Register={this.onPageChange}/>
          )
        }
    </div>
  );};
}

export default App;
