import React from 'react';

class SignInform extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			signinEmail: '',
			signinpassword: '',
	}

	}

	onEmailChange = (event) =>{
		this.setState({signinEmail:event.target.value})
	}

	onPasswordChange = (event) =>{
		this.setState({signinpassword:event.target.value})
	}

	onSubmitSignIn = () => {
		const {signinpassword, signinEmail} = this.state
		fetch('https://mysterious-hollows-03588.herokuapp.com/signin',{
			method: 'post',
			headers: {'Content-Type':'application/json'},
			body: JSON.stringify({
				email: signinEmail,
				password: signinpassword
			})
		})
			.then(res=>res.json())
			.then(user=>{
				if (user.id){
					this.props.loadUser(user);
					this.props.PageChange('sign_in')
				}
				
			})
	}

	render(){
		const {PageChange} = this.props;
		return(
		<article className="br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 center shadow-5">
			<main className="pa4 black-80">
			  <div className="measure">
			    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
			      <legend className="fw6 ph0 mh0 f1">Sign In</legend>
			      <div className="mt3">
			        <label className="db fw6 lh-copy f6" forhtml="email-address">Email</label>
			        <input 
			        className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
			        type="email" 
			        name="email-address"  
			        id="email-address"
			        onChange={this.onEmailChange}/>
			      </div>
			      <div className="mv3">
			        <label className="db fw6 lh-copy f6" forhtml="password">Password</label>
			        <input 
			        className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
			        type="password" 
			        name="password"  
			        id="password"
			        onChange={this.onPasswordChange}/>
			      </div>
			    </fieldset>
			    <div className="">
			      <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Sign in" onClick={this.onSubmitSignIn}/>
			    </div>
			    <div className="lh-copy mt3">
			      <p onClick={()=>PageChange('register')} className="f6 pointer dim black db">Register</p>
			    </div>
			  </div>
			</main>
		</article>
		);
	}
}

export default SignInform;
