import './App.css';
import React from 'react';
import Navigation from '../components/Navigation/Navigation.js';
import Logo from '../components/Logo/Logo.js';
import Rank from '../components/Rank/Rank.js';
import Input from '../components/Input/Input.js';
import Image from '../components/Image/Image.js';
import logo from './logo.svg';
import SignInForm from '../components/SignInForm/SignInForm.js';
import RegisterForm from '../components/RegisterForm/RegisterForm.js';
import Particles from 'react-tsparticles';
import particles_option from './particle_option.js';

class App extends React.Component{
  constructor(){
    super();
    this.state = {
      searchField : '',
      imgURL:logo,
      webpage: 'sign_out',
      user:
      {
        id: '',
        name: '',
        email: '',
        entries : '',
        joined: new Date()

      }
    }
  }

  loadUser = (user_) => {
    const {id, name, email, joined, entries} = user_;
    this.setState({user:{
      id: id,
      name: name,
      email: email,
      entries: entries,
      joined: joined
    }})
  }

  onSearchChange = (event) =>{
    this.setState({searchField:event.target.value})
  }

  onPageChange = (page) =>{
    this.setState({webpage:page})
  }

  onButtonClick = (event) => {
    this.setState({imgURL: this.state.searchField})
    fetch('http://localhost:3001/imgeurl',{
      method:'post',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({
        input: this.state.input
      })
    })
    .then(response=>response.json())
    .then(response=>{
      if (response){
        fetch('http://localhost:3001/image',{
          method: 'put',
          headers: {'Content-Type':'application/json'},
          body: JSON.stringify({
            id: this.state.user.id
          })
        })
          .then(res=>res.json())
          .then(count=>{
            this.setState(Object.assign(this.state.user,{entries:count}))
              })
          .catch(console.log)
          }
    }) 

  }

  render() {
    const {imgURL, webpage, user} = this.state;
    return(
    <div className="App">
        <Particles params={particles_option} />
        <Navigation PageChange={this.onPageChange} Webpage={webpage}/>
        {webpage ==='sign_in' ?
          <div>
            <Logo />
            <Rank name={user.name} rank={user.entries}/>
            <Input searchChange={this.onSearchChange} buttonClick={this.onButtonClick}/>
            <Image imgURL={imgURL}/>
          </div>:
          ( webpage === 'sign_out' ?
            <SignInForm PageChange={this.onPageChange} loadUser={this.loadUser}/>: 
            <RegisterForm Register={this.onPageChange} loadUser={this.loadUser}/>
          )
        }
    </div>
  );};
}

export default App;
